mapper.select:
select *
from user
where email = #{email}
  and pass_word = #{passWord}

mapper.update:
update user
first_name=#{firstName}, nick_name=#{nickName}, last_name=#{lastName}, email=#{email}, pass_word=#{passWord}, dob=#{dob}, gender=#{gender}, status=#{status}, hobbies=#{hobbies}, events=#{events}
where id=#{id}
